tool.command:
CLIENT=`cat /galaxy/creds.json | jq .illumina_basespace.client_id -r`;
SECRET=`cat /galaxy/creds.json | jq .illumina_basespace.secret_id -r`;
DEVICE_CODE=`cat $device_code_file | jq .device_code -r`;

TOKEN=`curl
	--silent
	-X POST
	-d"response_type=device_code"
	-d"client_id=\$CLIENT"
	-d"client_secret=\$SECRET"
	-d"code=\$DEVICE_CODE"
	-d"grant_type=device"
	https://api.basespace.illumina.com/v1pre3/oauthv2/token | jq .access_token -r`;

@USER_KEY@

TMPFILE=`mktemp`;
cat /galaxy/creds.basespace.json |
	jq ".basespace.\"\$USER_EMAIL_HASH\" = \"\$TOKEN\"" > \$TMPFILE;

mv \$TMPFILE /galaxy/creds.basespace.json;

printf '{"basespace": {"%s": "%s"}}' $__user_email__ \$TOKEN > $output;
